//! Stage Utilities

use crate::FJORD_MAX_SPAN_BATCH_BYTES;
use alloc::{vec, vec::Vec};
use alloc_no_stdlib::bzero;
use alloc_stdlib::HeapPrealloc;
use anyhow::{ensure, Result};
use brotli::*;

/// Decompresses the given bytes data using the Brotli decompressor implemented
/// in the [`brotli`](https://crates.io/crates/brotli) crate.
pub fn decompress_brotli(data: &[u8]) -> Result<Vec<u8>> {
    // Set up the Brotli state with the heap preallocators
    let mut u8_buffer = vec![0; 32 * 1024 * 1024].into_boxed_slice();
    let mut u32_buffer = vec![0; 1024 * 1024].into_boxed_slice();
    let mut hc_buffer = vec![HuffmanCode::default(); 4 * 1024 * 1024].into_boxed_slice();
    let heap_u8_allocator = HeapPrealloc::<u8>::new_allocator(4096, &mut u8_buffer, bzero);
    let heap_u32_allocator = HeapPrealloc::<u32>::new_allocator(4096, &mut u32_buffer, bzero);
    let heap_hc_allocator = HeapPrealloc::<HuffmanCode>::new_allocator(4096, &mut hc_buffer, bzero);
    let mut brotli_state =
        BrotliState::new(heap_u8_allocator, heap_u32_allocator, heap_hc_allocator);

    // Setup the decompressor inputs and outputs
    let mut output = vec![0; data.len()];
    let mut available_in = data.len();
    let mut input_offset = 0;
    let mut available_out = output.len();
    let mut output_offset = 0;
    let mut written = 0;

    // Decompress the data stream until success or failure
    loop {
        match brotli::BrotliDecompressStream(
            &mut available_in,
            &mut input_offset,
            data,
            &mut available_out,
            &mut output_offset,
            &mut output,
            &mut written,
            &mut brotli_state,
        ) {
            brotli::BrotliResult::ResultSuccess => break,
            brotli::BrotliResult::NeedsMoreOutput => {
                // Resize the output buffer to double the size, following standard
                // practice for buffer resizing in streams.
                let old_len = output.len();
                let new_len = old_len * 2;

                ensure!(new_len as u64 <= FJORD_MAX_SPAN_BATCH_BYTES, "Output buffer too large");

                output.resize(new_len, 0);
                available_out += old_len;
            }
            _ => break,
        }
    }

    // Truncate the output buffer to the written bytes
    output.truncate(written);

    Ok(output)
}

#[cfg(test)]
mod test {
    use super::*;
    use alloy_primitives::hex;

    #[test]
    fn test_decompress_brotli() {
        let expected = hex!("75ed184249e9bc19675e");
        let compressed = hex!("8b048075ed184249e9bc19675e03");

        let decompressed = decompress_brotli(&compressed).unwrap();
        assert_eq!(decompressed, expected);
    }

    #[test]
    fn test_decompress_batch_brotli() {
        let raw_batch_decompressed = hex!("");
        let raw_batch = hex!("");

        let decompressed = decompress_brotli(&raw_batch).unwrap();
        assert_eq!(decompressed, raw_batch_decompressed);
    }
}
